import React, { useState } from 'react';
import ProtCharacter from './ProtCharacter';

export default props => {
    const [deviation, setDeviation] = useState();

    function onKeyLeft() {
        setSpriteY(5);
        console.log('yee')
    }

    window.addEventListener('keydown', event => {
        if(event.keyCode === 37) setDeviation(-5);
        else if(event.keyCode === 39) setDeviation(5)
    });

    return <ProtCharacter x={50} y={props.y} deviation={deviation}
    />

}
