import React, { useState, useEffect } from 'react';
import prot from './prot.png'
import { Sprite, Image } from 'react-konva';

export default props => {
    const [image, setImage] = useState(null);

    useEffect(() => {
        const img = new window.Image();
        img.src = prot
        img.onload = () => {
            setImage(img)
        }
    })

    const animations = {
        idle: [0, 3, 4, 119, 71, 2, 74, 119, 146, 2, 81, 119, 226, 2, 76, 119],
        punch: [2, 138, 74, 122, 76, 138, 84, 122, 346, 138, 120, 122]
    }
    return <Sprite
        x={50}
        y={50}
        image={image}
        animation='idle'
        animations={animations}
        frameRate={7}
        frameIndex={0}
    />

}
