import React, { useState } from 'react';
import { Rect } from 'react-konva';
import Konva from 'konva';

export default props => {
    const { color, setColor } = useState('green')

    function handleClick() {
        setColor(Konva.Util.getRandomColor())
    }

    return <Rect
        x={20}
        y={20}
        width={1000}
        height={1000}
        fill={'green'}
        shadowBlur={5}
        onClick={handleClick}
    />
}
