import React, { useState, useEffect } from 'react';
import prot from '../assets/sprites/prot.png'
import { Sprite } from 'react-konva';

export default props => {
    const [image, setImage] = useState(null);
    const sprite = React.createRef();

    useEffect(() => {
        const img = new window.Image();
        img.src = prot
        img.onload = () => {
            setImage(img)
        }
        if(sprite.current) {
            // sprite.current.scaleX(5);
            // sprite.current.scaleY(5);
            sprite.current.start();
        } 
    })
    const animations = {
        idle: [
            //  x  y  w   h
            0, 3, 30, 40,
            30, 3, 30, 40,
            60, 3, 30, 40,
            90, 3, 30, 40
        ],
        punch: [2, 138, 74, 122, 76, 138, 84, 122, 346, 138, 120, 122]
    }

    console.log(image.height)

    return <Sprite
        ref={sprite}
        x={props.x}
        y={image ? props.y - image.height + 5 : 0}
        scaleX={5}
        scaleY={5}
        image={image}
        animation='idle'
        animations={animations}
        frameRate={7}
        frameIndex={0}
    />

}
