import React, { useState } from 'react';
import ProtCharacter from './ProtCharacter';

export default props => {
    const [spriteY, setSpriteY] = useState();

    function onKeyLeft() {
        setSpriteY(5);
        console.log('yee')
    }

    window.addEventListener('keydown', event => {
        if(event.keyCode === 37) setSpriteY(-5);
        else if(event.keyCode === 39) setSpriteY(5)
    });

    return <ProtCharacter x={50} y={props.y} deviation={0}
    />

}
