import React, { useState, useEffect } from 'react';
import prot from '../assets/sprites/prot.png'
import { Sprite, Image } from 'react-konva';

export default props => {
    const [image, setImage] = useState(null);
    const sprite = React.createRef();

    useEffect(() => {
        const img = new window.Image();
        img.src = prot
        img.onload = () => {
            setImage(img)
        }
    })

    const animations = {
        idle: [
            //  x  y  w   h
            0, 3, 30, 40,
            30, 3, 30, 40,
            60, 3, 30, 40,
            90, 3, 30, 40
        ],
        punch: [2, 138, 74, 122, 76, 138, 84, 122, 346, 138, 120, 122]
    }

    const spr = <Sprite
        ref={sprite}
        x={50}
        y={50}
        image={image}
        animation='idle'
        animations={animations}
        frameRate={7}
        frameIndex={0}
    />

    if (sprite.current) {
        sprite.current.start();
    }

    return spr;

}
